import React, { useState } from 'react'
import Select from './Select'


function App() {
  const [filters, setFilters] = useState({release_year: ""});

  const onChangeFilters = event => {
    const name = event.target.name;
    const value = event.target.value;
    setFilters({
      [name]: value
    });
  };

  return (
  <div className="App">
    <Select
      release_year={filters.release_year}
      onChangeFilters={onChangeFilters}
    />
    <p>Год выхода: {filters.release_year}</p>
  </div>
  )
}

export default App;
